INSERT INTO `frases` (`id`, `word`, `en_txt`, `pt_txt`, `tr_txt`) VALUES
(1, 'abandon', 'He decided to abandon his plans and leave town.', 'Ele decidiu abandonar seus planos e deixar a cidade.', '/hiː dɪˈsaɪdɪd tuː əˈbændən hɪz plænz ənd liːv taʊn/'),
